import React, { useState } from "react";
import "./Nav.css";
import { NavLink } from "react-router-dom";
import taxMapLogo from "../../assets/images/taxmap-official-logo.png";
function Nav() {
  // 카테고리 요소에 마우스 hover 시 보이는 세부 내용
  const [isHovering, setIsHovering] = useState({
    service: false,
    department: false,
  });

  return (
    <div>
      {/* NavBar*/}
      <div className="nav-bar">
        <a href="/">
          <img src={taxMapLogo} alt="taxMapLogo" className="taxMapLogo"></img>
        </a>
        <div className="links-container">
          <div
            className="nav-item"
            onMouseEnter={() => setIsHovering({ ...isHovering, service: true })}
            onMouseLeave={() =>
              setIsHovering({ ...isHovering, service: false })
            }
          >
            <NavLink to="/map/service" className="service-mode">
              서비스 모드
            </NavLink>
            {isHovering.service && (
              <div className="dropdown-content"> 세부 내용 </div>
            )}
          </div>
          <NavLink to="/map/department" className="department-mode">
            부처별 모드
          </NavLink>
        </div>
      </div>
    </div>
  );
}

export default Nav;
