import React, { useEffect } from "react";
import "./MapContent.css";

const { kakao } = window;
function MapContent() {
  // 스크립트 로드 후 kakao.map 객체 접근 및 지도 로드
  const script = document.createElement("script");
  script.src =
    "//dapi.kakao.com/v2/maps/sdk.js?appkey={process.env.REACT_APP_KAKAO_MAP_API_KEY}";
  document.head.appendChild(script); // index.html의 script에 추가되는 것인가?

  // 스크립트 로드 완료되면 지도를 초기화
  script.onload = () => {
    kakao.maps.load(() => {
      const container = document.getElementById("map-container");
      const options = {
        center: new kakao.maps.LatLng(37.55805491922956, 126.99832780535394), // 입력값 없을 경우, '동국대 신공학관'  위도 경도에 좌표 표시
        level: 4,
      };
    });
  };
  useEffect(() => {
    const map = new kakao.maps.Map(container, options);
  }, []);
  return <div className="map-container"></div>;
}
export default MapContent;
