import React, { useState } from "react";
import "./Nav.css";
import { NavLink } from "react-router-dom";
import taxMapLogo from "../../assets/images/taxmap-official-logo.png";
function Nav() {
  // 카테고리 요소에 마우스 hover 시 보이는 세부 내용
  const [isHovering, setIsHovering] = useState({
    service: false,
    department: false,
  });

  return (
    <div>
      {/* NavBar*/}
      <div className="nav-bar">
        <a href="/">
          <img src={taxMapLogo} alt="taxMapLogo" className="taxMapLogo"></img>
        </a>
        <div className="links-container">
          <div
            className="nav-item"
            onMouseEnter={() => setIsHovering({ ...isHovering, service: true })}
            onMouseLeave={() =>
              setIsHovering({ ...isHovering, service: false })
            }
          >
            <NavLink to="/map/service" className="service-mode">
              서비스 모드
            </NavLink>
            {isHovering.service && (
              <div className="dropdown-content"> 세부 내용 </div>
            )}
          </div>
          <div
            className="nav-item"
            onMouseEnter={() =>
              setIsHovering({ ...isHovering, department: true })
            }
            onMouseLeave={() =>
              setIsHovering({ ...isHovering, department: false })
            }
          >
            <NavLink to="/map/department" className="department-mode">
              부처별 모드
            </NavLink>
            {isHovering.department && (
              <div className="dropdown-content">
                <p>행정 / 통일 / 외교</p>
                <p>사회복지향상</p>
                <p>국토개발지원</p>
                <p>안정보장</p>
                <p>보훈향상</p>
                <p>교통 / 물류진흥</p>
                <p>교육 보장</p>
                <p>고용안정</p>
                <p>방송 / 통신진흥</p>
                <p>문화활동</p>
                <p>주거안정</p>
                <p>과학기술진흥</p>
                <p>관광 / 휴양활동</p>
                <p> 보건 / 의료</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Nav;
