import React, { useEffect, useRef, useState } from "react";
import "./MapContent.css";
import Sidebar from "../Sidebar/Sidebar";

function MapContent({ coordinates, selectedDepartment }) {
  const mapContainerRef = useRef(null); // DOM에 접근하기 위한 ref 객체 생성

  // 마커 데이터 저장
  const [markers, setMarkers] = useState([]);

  // 선택된 마커의 정보를 사이드바에 출력 목적
  const [selectedMarker, setSelectedMarker] = useState(null);

  // 사이드 바 닫기
  const closeSidebar = () => {
    setSelectedMarker(null);
  };

  useEffect(() => {
    // 스크립트 로드 후 kakao.map 객체 접근 및 지도 로드, autofalse로 자동 로드 방지
    const script = document.createElement("script");
    script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=${process.env.REACT_APP_KAKAO_MAP_API_KEY}&autoload=false`;
    document.head.appendChild(script);

    script.onload = () => {
      window.kakao.maps.load(() => {
        // 기본 값으로 '동국대 신공학관' 위도 경도에 좌표 표시
        const options = {
          center: new window.kakao.maps.LatLng(
            coordinates ? coordinates.lat : 37.55805491922956, // 사용자 선택 좌표 : 디폴트 좌표(신공학관)
            coordinates ? coordinates.lng : 126.99832780535394
          ),
          level: 4,
        };

        const map = new window.kakao.maps.Map(mapContainerRef.current, options);

        const fetchAndDisplayMarkers = async () => {
          const bounds = map.getBounds();
          const swLatLng = bounds.getSouthWest();
          const neLatLng = bounds.getNorthEast();

          // API로 경계 정보 전송하여 마커 데이터를 요청
          const requestBody = {
            minLat: swLatLng.getLat(),
            maxLat: neLatLng.getLat(),
            minLng: swLatLng.getLng(),
            maxLng: neLatLng.getLng(),
            govOfficeFilter: selectedDepartment, // 부처 필터 추가
          };

          console.log("Request body:", requestBody); // 요청 데이터 로그 출력

          try {
            const response = await fetch(
              `${process.env.REACT_APP_API_URL}/api/map/view`,
              {
                method: "POST",
                headers: {
                  "Content-Type": "application/json",
                },
                body: JSON.stringify(requestBody),
              }
            );

            if (!response.ok) {
              throw new Error("Failed to fetch markers");
            }

            const data = await response.json();
            console.log("Fetched markers data:", data); // 응답 데이터 로그 출력

            const filteredMarkers = data.filter((markerData) =>
              markerData.organizations.some((org) =>
                org.subsidies.some(
                  (subsidy) => subsidy.govOffice === selectedDepartment
                )
              )
            );
            console.log("Filtered markers data:", filteredMarkers); // 필터링된 마커 데이터 로그 출력

            setMarkers(filteredMarkers);

            // 필터링된 마커 지도에 추가
            filteredMarkers.forEach((markerData) => {
              const marker = new window.kakao.maps.Marker({
                map: map,
                position: new window.kakao.maps.LatLng(
                  markerData.latitude,
                  markerData.longitude
                ),
                title: markerData.address,
              });

              // 마커 클릭 이벤트 핸들러
              window.kakao.maps.event.addListener(marker, "click", async () => {
                try {
                  const organizationsWithSubsidies = await Promise.all(
                    (markerData.organizations || []).map(async (org) => {
                      const subsidyResponse = await fetch(
                        `${process.env.REACT_APP_API_URL}/api/subsidy/${org.id}`
                      );
                      if (subsidyResponse.ok) {
                        const subsidyData = await subsidyResponse.json();
                        return { ...org, subsidies: subsidyData };
                      }
                      return org;
                    })
                  );

                  setSelectedMarker({
                    ...markerData,
                    organizations: organizationsWithSubsidies,
                  });
                } catch (error) {
                  console.error("Error fetching subsidy details:", error);
                }
              });
            });
          } catch (error) {
            console.error("Error fetching organizations within bounds:", error);
          }
        };

        window.kakao.maps.event.addListener(
          map,
          "bounds_changed",
          fetchAndDisplayMarkers
        );
        fetchAndDisplayMarkers();
      });
    };
  }, [coordinates, selectedDepartment]);

  return (
    <div>
      <div ref={mapContainerRef} className="map-container"></div>
      {selectedMarker && (
        <Sidebar marker={selectedMarker} onClose={closeSidebar} />
      )}
    </div>
  );
}

export default MapContent;
