import React from "react";
import "./Sidebar.css";

const Sidebar = ({ marker, onClose }) => {
  if (!marker) return null; // 데이터 없으면 렌더링 안함

  return (
    <div className="sidebar">
      <button onClick={onClose} className="close-btn">
        ✖
      </button>
      <h2>{marker.name}</h2>
      <div>주소 : {marker.address}</div>
      <div>
        총 보조금:{" "}
        {new Intl.NumberFormat("ko-KR", {
          style: "currency",
          currency: "KRW",
        }).format(marker.maxTotalSubsidy)}
      </div>
      <div>부처: {marker.govOffice || "정보 없음"}</div>
      {marker.subsidies &&
        marker.subsidies.length > 0 && ( // 추가: 보조사업 정보가 있을 경우 출력
          <div className="subsidies-container">
            <h3>보조사업 정보:</h3>
            {marker.subsidies.map((subsidy, index) => (
              <div key={index} className="subsidy-detail">
                <h4>{subsidy.businessName}</h4>
                <p>연도: {subsidy.businessYear}</p>
                <p>
                  금액:{" "}
                  {new Intl.NumberFormat("ko-KR", {
                    style: "currency",
                    currency: "KRW",
                  }).format(subsidy.govExpense)}
                </p>
                <p>설명: {subsidy.businessDescription}</p>
              </div>
            ))}
          </div>
        )}
    </div>
  );
};

export default Sidebar;
