import React, { useEffect, useState } from "react";
import "./Sidebar.css";

const Sidebar = ({ marker, onClose }) => {
  const [subsidies, setSubsidies] = useState([]);

  useEffect(() => {
    const fetchSubsidies = async () => {
      try {
        const allSubsidies = await Promise.all(
          marker.organizations.map(async (org) => {
            const response = await fetch(
              `${process.env.REACT_APP_API_URL}/api/subsidy/${org.id}`
            );
            if (!response.ok) {
              throw new Error(`Failed to fetch subsidies for org ${org.id}`);
            }
            const data = await response.json();
            return {
              orgName: org.orgName,
              subsidies: Array.isArray(data) ? data : [],
            };
          })
        );
        setSubsidies(allSubsidies);
      } catch (error) {
        console.error("Error fetching subsidies:", error);
      }
    };

    if (marker) {
      fetchSubsidies();
    }
  }, [marker]);

  if (!marker) return null;

  return (
    <div className="sidebar">
      <button onClick={onClose} className="close-btn">
        ✖
      </button>
      <h2>{marker.name}</h2>
      <div>주소 : {marker.address}</div>
      <div>
        총 보조금:{" "}
        {new Intl.NumberFormat("ko-KR", {
          style: "currency",
          currency: "KRW",
        }).format(marker.maxTotalSubsidy)}
      </div>
      <div>
        부처:{" "}
        {marker.govOffices && marker.govOffices.length > 0
          ? marker.govOffices.join(", ")
          : "정보 없음"}
      </div>
      {subsidies.length > 0 ? (
        <div className="organizations-container">
          <h3>조직 상세 정보: </h3>
          {subsidies.map((orgData, index) => (
            <div key={index} className="organization-detail">
              <h4>{orgData.orgName}</h4>
              {orgData.subsidies.map((subsidy, subIndex) => (
                <div key={subIndex}>
                  <div>부처: {subsidy.govOffice || "정보 없음"}</div>
                  <div>재정연도: {subsidy.financialYear || "정보 없음"}</div>
                  <div>사업연도: {subsidy.businessYear || "정보 없음"}</div>
                  <div>사업명: {subsidy.businessName || "정보 없음"}</div>
                  <div>
                    총 사업비:{" "}
                    {new Intl.NumberFormat("ko-KR", {
                      style: "currency",
                      currency: "KRW",
                    }).format(subsidy.totalBusinessExpense || 0)}
                  </div>
                  <div>
                    정부 지원금:{" "}
                    {new Intl.NumberFormat("ko-KR", {
                      style: "currency",
                      currency: "KRW",
                    }).format(subsidy.govExpense || 0)}
                  </div>
                  <div>
                    지방 자치단체 지원금:{" "}
                    {new Intl.NumberFormat("ko-KR", {
                      style: "currency",
                      currency: "KRW",
                    }).format(subsidy.localExpense || 0)}
                  </div>
                  <div>
                    자부담금:{" "}
                    {new Intl.NumberFormat("ko-KR", {
                      style: "currency",
                      currency: "KRW",
                    }).format(subsidy.selfExpense || 0)}
                  </div>
                  <div>
                    신청 보조금: {subsidy.requestedSubsidy || "정보 없음"}
                  </div>
                  <div>
                    정부 예산:{" "}
                    {new Intl.NumberFormat("ko-KR", {
                      style: "currency",
                      currency: "KRW",
                    }).format(subsidy.govBudget || 0)}
                  </div>
                  <div>
                    지방 자치단체 예산:{" "}
                    {new Intl.NumberFormat("ko-KR", {
                      style: "currency",
                      currency: "KRW",
                    }).format(subsidy.localBudget || 0)}
                  </div>
                  <div>
                    지급된 보조금:{" "}
                    {new Intl.NumberFormat("ko-KR", {
                      style: "currency",
                      currency: "KRW",
                    }).format(subsidy.requestedPaid || 0)}
                  </div>
                  <div>
                    권한 지급금:{" "}
                    {new Intl.NumberFormat("ko-KR", {
                      style: "currency",
                      currency: "KRW",
                    }).format(subsidy.authorityPaid || 0)}
                  </div>
                  <div>사업 목적: {subsidy.businessPurpose || "정보 없음"}</div>
                  <div>
                    사업 설명: {subsidy.businessDescription || "정보 없음"}
                  </div>
                  <div>
                    사업 기간: {subsidy.businessDuration || "정보 없음"}
                  </div>
                  <div>
                    사업 위치: {subsidy.businessLocation || "정보 없음"}
                  </div>
                </div>
              ))}
            </div>
          ))}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Sidebar;
