import React, { useEffect, useRef, useState } from "react";
import "./MapContent.css";
import Sidebar from "../Sidebar/Sidebar";

function MapContent({ coordinates, selectedDepartment }) {
  const mapContainerRef = useRef(null); // DOM에 접근하기 위한 ref 객체 생성

  // 마커 데이터 저장
  const [markers, setMarkers] = useState([]);
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [map, setMap] = useState(null);

  // 사이드 바 닫기
  const closeSidebar = () => {
    setSelectedMarker(null);
  };

  useEffect(() => {
    const script = document.createElement("script");
    script.src = `//dapi.kakao.com/v2/maps/sdk.js?appkey=${process.env.REACT_APP_KAKAO_MAP_API_KEY}&autoload=false`;
    document.head.appendChild(script);

    script.onload = () => {
      window.kakao.maps.load(() => {
        const defaultCenter = new window.kakao.maps.LatLng(
          37.55805491922956,
          126.99832780535394
        );

        const options = {
          center: coordinates
            ? new window.kakao.maps.LatLng(coordinates.lat, coordinates.lng)
            : defaultCenter,
          level: 4,
        };

        const newMap = new window.kakao.maps.Map(
          mapContainerRef.current,
          options
        );
        setMap(newMap);

        window.kakao.maps.event.addListener(newMap, "bounds_changed", () => {
          fetchAndDisplayMarkers(newMap);
        });

        fetchAndDisplayMarkers(newMap);
      });
    };
  }, [coordinates]);

  useEffect(() => {
    if (map) {
      fetchAndDisplayMarkers(map);
    }
  }, [selectedDepartment]);

  const fetchAndDisplayMarkers = async (mapInstance) => {
    const bounds = mapInstance.getBounds();
    const swLatLng = bounds.getSouthWest();
    const neLatLng = bounds.getNorthEast();

    const requestBody = {
      minLat: swLatLng.getLat(),
      maxLat: neLatLng.getLat(),
      minLng: swLatLng.getLng(),
      maxLng: neLatLng.getLng(),
    };

    console.log("Request body:", requestBody);

    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/api/map/view`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(requestBody),
        }
      );

      if (!response.ok) {
        throw new Error("Failed to fetch markers");
      }

      const data = await response.json();
      console.log("Fetched markers data:", data);

      // 기존 마커를 지도에서 제거
      markers.forEach((marker) => marker.setMap(null));

      // 새로운 마커 설정
      const newMarkers = [];

      for (const markerData of data) {
        const organizationsWithSubsidies = await Promise.all(
          (markerData.organizations || []).map(async (org) => {
            const subsidyResponse = await fetch(
              `${process.env.REACT_APP_API_URL}/api/subsidy/${org.id}`
            );
            if (subsidyResponse.ok) {
              const subsidyData = await subsidyResponse.json();
              return { ...org, subsidies: subsidyData };
            }
            return org;
          })
        );

        const hasMatchingSubsidy = organizationsWithSubsidies.some(
          (org) =>
            Array.isArray(org.subsidies) &&
            org.subsidies.some(
              (subsidy) => subsidy.govOffice === selectedDepartment
            )
        );

        // 부처가 선택되지 않았거나, 선택된 부처와 일치하는 마커만 추가
        if (!selectedDepartment || hasMatchingSubsidy) {
          const marker = new window.kakao.maps.Marker({
            map: mapInstance,
            position: new window.kakao.maps.LatLng(
              markerData.latitude,
              markerData.longitude
            ),
            title: markerData.address,
          });

          window.kakao.maps.event.addListener(marker, "click", () => {
            setSelectedMarker({
              ...markerData,
              organizations: organizationsWithSubsidies,
            });
          });

          newMarkers.push(marker);
          console.log("Marker created:", marker);
        }
      }

      setMarkers(newMarkers);
    } catch (error) {
      console.error("Error fetching organizations within bounds:", error);
    }
  };

  return (
    <div>
      <div ref={mapContainerRef} className="map-container"></div>
      {selectedMarker && (
        <Sidebar marker={selectedMarker} onClose={closeSidebar} />
      )}
    </div>
  );
}

export default MapContent;
